  }
}

@layer utilities {
  .bg-gradient-primary {
    background: linear-gradient(135deg, hsl(238, 74%, 60%) 0%, hsl(265, 75%, 58%) 100%);
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(238, 74%, 60%) 0%, hsl(265, 75%, 58%) 100%);
