    background: rgba(0, 0, 0, 0.8);
  }

  /* Gradient border utility */
  .gradient-border {
    background: linear-gradient(135deg, hsl(238, 74%, 60%), hsl(265, 75%, 58%));
    padding: 2px;
    border-radius: calc(var(--radius) + 2px);
  }

