  }

  /* Custom gradient backgrounds */
  .gradient-primary {
    background: linear-gradient(135deg, hsl(238, 74%, 60%), hsl(265, 75%, 58%));
  }

  .gradient-success {
    background: linear-gradient(135deg, hsl(159, 88%, 35%), hsl(120, 60%, 50%));
  }

  .gradient-warning {
    background: linear-gradient(135deg, hsl(35, 91%, 56%), hsl(45, 93%, 47%));
