  }

  /* Portfolio hero gradient */
  .portfolio-hero {
    background: linear-gradient(135deg, hsl(238, 74%, 60%) 0%, hsl(265, 75%, 58%) 100%);
  }
}

@layer utilities {
  /* Custom utility classes */
  .text-gradient {
    background: linear-gradient(135deg, hsl(238, 74%, 60%), hsl(265, 75%, 58%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Border gradient utilities */
  .border-gradient {
    border: 1px solid transparent;
    background: linear-gradient(hsl(var(--background)), hsl(var(--background))) padding-box,
                linear-gradient(135deg, hsl(238, 74%, 60%), hsl(265, 75%, 58%)) border-box;
  }

  /* Shadow utilities */
  .shadow-primary {
