// PDF export functionality using browser's print API
// In a real implementation, you might want to use react-pdf or similar

export interface ProfileData {
  name: string;
  role: string;
  email: string;
  phone: string;
  location: string;
  summary: string;
}

export interface ResumeData {
  profile: ProfileData;
  workExperience: Array<{
    title: string;
    company: string;
    startDate: string;
    endDate?: string;
    description: string;
  }>;
  education: Array<{
    degree: string;
    institution: string;
